import React, { Component } from 'react';
import './App.css';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import About from "./components/about";
import Projects from "./components/projects";
import Main from "./components/main";

class App extends Component {
  state = {
    activePage: "main"
  }

  aboutLink = () => {
    this.setState({activePage: "about"})
  }

  projectsLink = () => {
    this.setState({activePage: "projects"})
  }

  mainLink = () => {
    this.setState({activePage: "main"})
  }

  showActivePage = () => {
    if(this.state.activePage === "about") {
      return (<About />)
    } else if(this.state.activePage === "projects") {
      return (<Projects />)
    } else if(this.state.activePage === "main") {
      return (<Main />)
    }
  }

  render() {
    return (
      <BrowserRouter>
        <ul>
          <li><div onClick={this.mainLink}>main</div></li>
          <li><div onClick={this.aboutLink}>about</div></li>
          <li><div onClick={this.projectsLink}>projects</div></li>
        </ul>
        <Route path = '/' component = {Main} />

        </BrowserRouter>
    );
  }
}

export default App;
